					from source.archive to target.archive;  
			}  
			perform manageAlias.syncAlias {  
				in carAlias = aliasCmdPort.carAlias;  
				out addDoc = documentCmdPort.addDoc;  
				out query = documentCmdPort.query;  
				out archive = documentCmdPort.archive;  
			}  
		}  
		part physicalQuantityManager {  
			port quantityPort: ~QuantityPort;  
			perform managePhysicalQuantity.referenceStandard {  
				in quantity = quantityPort.quantity;  
			}  
		}  
		part torqueManager {  
			port torquePort: ~TorquePort;  
			perform manageTorque.applyISQStandard {  
				in torque = torquePort.torque;  
			}  
		}  
		part documentRepository {  
			port documentCmdPort: ~DocumentCmdPort;  
			port contentPort: DocumentContentPort;  
			perform manageDocuments.storeDocument {  
				in addDoc = documentCmdPort.addDoc;  
				out content = contentPort.content;  
			}  
			perform manageDocuments.retrieveDocument {  
				in query = documentCmdPort.query;  
				out content = contentPort.content;  
			}  
			perform manageDocuments.storeArchive {  
				in archive = documentCmdPort.archive;  
			}  
		}  
	}  
	action manageDocuments {  
		action sendDocumentCmd { out addDoc: AutomobileInfo; out query: AutomobileInfo; out archive: AutomobileInfo; }  
		action addDocument { in addDoc: AutomobileInfo; out quantity: PhysicalQuantity; out torque: TorqueValue; }  
		action queryDocument { in query: AutomobileInfo; out quantity: PhysicalQuantity; out torque: TorqueValue; }  
		action archiveDocument { in archive: AutomobileInfo; }  
		action storeDocument { in addDoc: AutomobileInfo; out content: ExplanatoryDocument; }  
		action retrieveDocument { in query: AutomobileInfo; out content: ExplanatoryDocument; }  
		action storeArchive { in archive: AutomobileInfo; }  
		succession flow addDocumentFlow from sendDocumentCmd.addDoc to addDocument.addDoc;  
		succession flow queryFlow from sendDocumentCmd.query to queryDocument.query;  
		succession flow archiveFlow from sendDocumentCmd.archive to archiveDocument.archive;  
		succession flow documentStorageFlow from addDocument.addDoc to storeDocument.addDoc;  
		succession flow documentRetrievalFlow from queryDocument.query to retrieveDocument.query;  
		succession flow archiveStorageFlow from archiveDocument.archive to storeArchive.archive;  
	}  
	action managePhysicalQuantity {  
		action setStandard { out quantity: PhysicalQuantity; }  
		action referenceStandard { in quantity: PhysicalQuantity; }  
		succession flow quantityFlow from setStandard.quantity to referenceStandard.quantity;  
	}  
	action manageTorque {  
		action defineTorque { out torque: TorqueValue; }  
		action applyISQStandard { in torque: TorqueValue; }  
		succession flow torqueFlow from defineTorque.torque to applyISQStandard.torque;  
	}  
	action manageAlias {  
		action sendCarAlias { out carAlias: CarInfo; }  
		action syncAlias { in carAlias: CarInfo; out addDoc: AutomobileInfo; out query: AutomobileInfo; out archive: AutomobileInfo; }  
		action aliasFlow { from sendCarAlias.carAlias to syncAlias.carAlias; }  
	}  
}

package 'AutomobileSystem' {
	attribute def Automobile;
	attribute def Car = Automobile;
	attribute def TorqueValue;
	attribute def Torque = TorqueValue;
	port def TorquePort {
		out torque: TorqueValue;
	}
	part context {
		part automobile {
			alias Car;
			port torquePort: TorquePort;
			perform manageAutomobileData.receiveTorque {
				in torque = torquePort.torque;
			}
		}
		part physicalQuantityImporter {
			port torquePort: ~TorquePort;
			perform manageAutomobileData.importTorque {
				out torque = torquePort.torque;
			}
		}
		interface automobileToImporter connect automobile.torquePort to physicalQuantityImporter.torquePort {
			ref flow references manageAutomobileData.torqueFlow
				from source.torque to target.torque;
		}
	}
	action manageAutomobileData {
		action importTorque { out torque: TorqueValue; }
		succession flow torqueFlow from importTorque.torque to receiveTorque.torque;
		action receiveTorque { in torque: TorqueValue; }
	}
}

package 'VehicleSystem' {
    value type CylinderCount;
    block def Vehicle;
    block def Engine;
    block def Cylinder;

    part context {
        part smallVehicle {
            part engine: Engine;
            constraint c1: engine.cylinders->size() = 4;
        }
        part largeVehicle {
            part engine: Engine;
            constraint c2: engine.cylinders->size() = 6;
        }
    }

    block def Vehicle {
        part engine: Engine;
    }

    block def Engine {
        part cylinders: Cylinder[4..6];
    }
}

package 'FuelManagementSystem' {
	attribute def FuelSupply;
	attribute def FuelReturn;
	attribute def FuelTemperature;
	port def FuelSupplyPort {
		out fuelSupply : FuelSupply;
	}
	port def FuelReturnPort {
		out fuelReturn : FuelReturn;
	}
	port def TemperaturePort {
		out temperature : FuelTemperature;
	}
	part context {
		part fuelTank {
			port fuelSupplyPort: FuelSupplyPort;
			perform fuelManagementRegion.deliverFuel {
				out fuelSupply = fuelSupplyPort.fuelSupply;
			}
		}
		interface fuelTankToInterface connect fuelTank.fuelSupplyPort to fuelInterfaces.fuelSupplyPort {
			ref flow references fuelManagementRegion.fuelSupplyFlow
				from source.fuelSupply to target.fuelSupply;
		}
		interface fuelInterfacesToEngine connect fuelInterfaces.fuelReturnPort to engine.fuelReturnPort {
			ref flow references fuelManagementRegion.fuelReturnFlow
				from source.fuelReturn to target.fuelReturn;
		}
		part fuelInterfaces {
			port fuelSupplyPort: ~FuelSupplyPort;
			port fuelReturnPort: ~FuelReturnPort;
		}
		part engine {
			port fuelSupplyPort: FuelSupplyPort;
			port fuelReturnPort: FuelReturnPort;
			perform fuelManagementRegion.consumeFuel {
				in fuelSupply = fuelSupplyPort.fuelSupply;
				out fuelReturn = fuelReturnPort.fuelReturn;
			}
		}
		part fuelManagementSystem {
			port temperaturePort: TemperaturePort;
			port fuelSupplyPort: FuelSupplyPort;
			port fuelReturnPort: FuelReturnPort;
			perform fuelManagementRegion.monitorTemperature {
				out temperature = temperaturePort.temperature;
			}
			perform fuelManagementRegion.manageFuelFlow {
				in temperature = temperaturePort.temperature;
				out fuelSupply = fuelSupplyPort.fuelSupply;
				out fuelReturn = fuelReturnPort.fuelReturn;
			}
		}
	}
	action fuelManagementRegion {
		action deliverFuel { out fuelSupply: FuelSupply; }
		action consumeFuel { in fuelSupply: FuelSupply; out fuelReturn: FuelReturn; }
		succession flow fuelSupplyFlow from manageFuelFlow.fuelSupply to deliverFuel.fuelSupply;
		succession flow fuelReturnFlow from manageFuelFlow.fuelReturn to consumeFuel.fuelReturn;
		action monitorTemperature { out temperature: FuelTemperature; }
		succession flow temperatureFlow from monitorTemperature.temperature to manageFuelFlow.temperature;
		action manageFuelFlow { 
			in temperature: FuelTemperature; 
			out fuelSupply: FuelSupply; 
			out fuelReturn: FuelReturn; 
		}
	}
}

package 'VehicleStructuralArchitecture' {
	attribute def Mass;
	attribute def SteeringAngle;
	part def Vehicle {
		attribute mass: Mass;
		part frontAxleAssembly: AxleAssembly;
		part rearAxleAssembly: AxleAssembly;
	}
	part def AxleAssembly {
		part axle: Axle;
		part leftWheel: Wheel;
		part rightWheel: Wheel;
	}
	part def Axle {
	}
	part def FrontAxle extends Axle {
		attribute steeringAngle: SteeringAngle;
	}
	part def RearAxle extends Axle {
	}
	part def Wheel {
	}
	part context {
		part vehicle {
			part frontAxleAssembly: AxleAssembly {
				part axle: FrontAxle;
				part leftWheel: Wheel;
				part rightWheel: Wheel;
			};
			part rearAxleAssembly: AxleAssembly {
				part axle: RearAxle;
				part leftWheel: Wheel;
				part rightWheel: Wheel;
			};
		}
	}
}
