model TrafficSignalSystem {
  component TrafficSignal {
    state machine ColorState {
      initial state Initial {
        entry: set color to Green;
      }
      state Green {
        entry: set color to Green;
      }
      state Yellow {
        entry: set color to Yellow;
      }
      state Red {
        entry: set color to Red;
      }
      transition Initial -> Green {
        trigger: start;
      }
      transition Green -> Yellow {
        trigger: changeToYellow;
      }
      transition Yellow -> Red {
        trigger: changeToRed;
      }
      transition Red -> Green {
        trigger: changeToGreen;
      }
    }
  }

  component AlwaysGreenTrafficSignal {
    state machine ColorState {
      initial state Initial {
        entry: set color to Green;
      }
      state Green {
        entry: set color to Green;
      }
    }
  }
}

