model System {
  interface pe;
  interface pf;
  component c1 {
    port pb: pe;
    port pa: Signal;
  };
  component c2 {
    port pb: Signal;
  };
  component c3 {
    port pd: pf;
    port pa: Signal;
  };
  component c4 {
    port pb: Signal;
  };
  model b11 {
    part c1: Component;
    part c2: Component;
    connection c1.pa -> c2.pb;
    bind c1.pb to pe;
  };
  model b12 {
    part c3: Component;
    part c4: Component;
    connection c3.pa -> c4.pb;
    bind c3.pd to pf;
  };
  breakdown b11, b12;
  part b11: Subsystem;
  part b12: Subsystem;
  connection b11.pe -> b12.pf;
  model a11 {
    part c1: Component;
    part c2: Component;
    part c3: Component;
    part c4: Component;
  };
  model b21 {
    part c1: Component ref a11.c1;
    part c3: Component ref a11.c3;
  };
  model b22 {
    part c2: Component ref a11.c2;
    part c4: Component ref a11.c4;
  };
  breakdown b21, b22;
  part b21: Module;
  part b22: Module;
  part a11: System;
}

