block PowertrainSystem {
  part engine : Engine;
  part torqueAmplifier : TorqueAmplifier;
  part transmission : Transmission;
  part driveWheels : DriveWheels;
  property fuelControlCommand : FuelControlCommand;
  property stallSignal : StallSignal;

  operation startEngine() {
    self.engine.start();
  }

  operation shutdownEngine() {
    self.engine.shutdown();
  }

  operation generateTorque() {
    self.engine.generatedTorque = self.engine.calculateTorque(self.fuelControlCommand);
  }

  operation amplifyTorque() {
    self.torqueAmplifier.amplifiedTorque = self.torqueAmplifier.amplify(self.engine.generatedTorque);
  }

  operation transmitTorque() {
    self.transmission.transmittedTorque = self.transmission.transmit(self.torqueAmplifier.amplifiedTorque);
  }

  operation distributeTorque() {
    self.driveWheels.outputTorque = self.transmission.transmittedTorque;
  }

  constraint c1 : StallCondition;
}

block Engine {
  operation start();
  operation shutdown();
  operation calculateTorque(fuelCommand : FuelControlCommand) : Torque;
  property generatedTorque : Torque;
}

block TorqueAmplifier {
  operation amplify(inputTorque : Torque) : Torque;
  property amplifiedTorque : Torque;
}

block Transmission {
  operation transmit(inputTorque : Torque) : Torque;
  property transmittedTorque : Torque;
}

block DriveWheels {
  property outputTorque : Torque;
  operation distribute(inputTorque : Torque) {
    self.outputTorque = inputTorque;
  }
}

signal FuelControlCommand;
signal StallSignal;constraint StallCondition {
  context PowertrainSystem;
  self.stallSignal.implies(self.engine.isStopped and self.torqueAmplifier.isIdle and self.transmission.isNeutral and self.driveWheels.isLocked);
}
