model FuelDeliverySystem {  
  flow FuelFlow {  
    property fuelQuantity : Real;  
  }  

  block StorageTank {  
    port out fuelOut : FlowPort<FuelFlow>;  
    property capacity : Real;  
    property currentLevel : Real;  
  }  

  block FuelPump {  
    port in fuelIn : FlowPort<FuelFlow>;  
    port out fuelOut : FlowPort<FuelFlow>;  
  }  

  block VehicleFuelTank {  
    port in fuelIn : FlowPort<FuelFlow>;  
    property capacity : Real;  
    property currentLevel : Real;  
  }  

  block FuelDeliverySystem {  
    part storageTank : StorageTank;  
    part fuelPump : FuelPump;  
    part vehicleFuelTank : VehicleFuelTank;  

    connection storageToPump : fuelOut(storageTank) -> fuelIn(fuelPump);  
    connection pumpToVehicle : fuelOut(fuelPump) -> fuelIn(vehicleFuelTank);  

    constraint maxCapacityConstraint : vehicleFuelTank.currentLevel <= vehicleFuelTank.capacity;  
    constraint levelUpdateConstraint : vehicleFuelTank.currentLevel = vehicleFuelTank.currentLevel@pre + fuelOut(fuelPump).fuelQuantity;  
  }  
}

