signal startSignal;
signal vehicleOnSignal;
signal restoreToNormalSignal;
signal overTemperatureSignal;
signal maintenanceSignal;

stateMachine VehicleState {
  initial state off {
    entry / performInitialStartupChecks;
  }
  state starting {
    entry / initiateStartingSequence;
  }
  state running {
    entry / performSelfChecks, supplyPower;
    exit / applyParkingBrake;
  }
  state degraded {
    entry / enterDegradedMode;
    exit / exitDegradedMode;
  }
  state maintenance {
    entry / enterMaintenanceMode;
    exit / exitMaintenanceMode;
  }
  state normal {
    entry / resumeNormalOperation;
  }
  transition off -> starting {
    trigger startSignal;
    guard brakePressed;
  }
  transition starting -> running {
    trigger vehicleOnSignal;
  }
  transition running -> degraded {
    trigger temperatureExceedsThreshold;
    action send overTemperatureSignal;
  }
  transition degraded -> normal {
    trigger restoreToNormalSignal;
  }
  transition running -> maintenance {
    trigger maintenanceSignal;
  }
  transition maintenance -> normal {
    trigger restoreToNormalSignal;
  }
}

stateMachine ControllerState {
  initial state idle {
    entry / awaitStartupSignal;
  }
  state active {
    entry / manageVehicleStates;
  }
  transition idle -> active {
    trigger startSignal;
  }
  transition active -> idle {
    trigger shutdownSignal;
  }
}

model VehicleSystem {
  part vehicle : Vehicle;
  part vehicleController : VehicleController;

  property fuel : String;
  property maintenanceInterval : Duration;
  property temperatureThreshold : Temperature;

  stateMachine VehicleStateMachine {
    initial state shutdown;
    state operational;
    state degraded;

    transition shutdown -> operational {
      trigger brakePedalPressed, startSignalReceived;
      activity selfInspection, powerSupply;
    }

    transition operational -> degraded {
      trigger temperature > temperatureThreshold;
      activity sendWarningSignal(vehicleController), ensureSafety;
    }

    transition degraded -> operational {
      trigger faultRecoveryCompleted;
      activity restoreNormalOperation;
    }
  }

  behavior selfInspection {
    do {
      // Execute vehicle self-inspection procedures
    }
  }

  behavior powerSupply {
    do {
      // Complete power supply initialization
    }
  }

  behavior sendWarningSignal(input vehicleController : VehicleController) {
    do {
      vehicleController.receiveWarningSignal();
    }
  }

  behavior ensureSafety {
    do {
      // Implement safety protocols in degraded mode
    }
  }

  behavior receiveWarningSignal() {
    do {
      // Process warning signal and initiate fault handling
    }
  }

  behavior restoreNormalOperation {
    do {
      // Execute fault recovery and return to normal mode
    }
  }

  behavior handleMaintenance(input interval : Duration) {
    do {
      if (timeSinceLastMaintenance >= interval) {
        perform scheduledMaintenance;
      }
    }
  }
}

