model ModularVehiclePlatform {
  block System {
    part subsystemA : SubsystemA;
    part subsystemB : SubsystemB;
    connection subsystemA.portA -> subsystemB.portB;
    apply SystemConstraint;
  }

  block SubsystemA {
    part part1 : Part1;
    part part2 : Part2;
    part part3 : Part3 [1..3];
    part part4 : Part4;
    port portA;
  }

  block SubsystemB {
    part part5 : Part5 [1..2];
    part part6 : Part6;
    port portB;
  }

  block Part3 {
    port p1;
  }

  block Part5 {
    port p2;
    behavior behaviorVariant1;
    behavior behaviorVariant2;
  }

  block Part1 {}
  block Part2 {}
  block Part4 {}
  block Part6 {}

  variantSet VehicleConfigA {
    select System.subsystemA.part1;
    select System.subsystemA.part2;
    set System.subsystemA.part3.quantity = 2;
    select System.subsystemB.part4;
    select System.subsystemB.part5;
    set System.subsystemB.part5.behaviorVariant1;
  }

  variantSet VehicleConfigB {
    select System.subsystemA.part4;
    select System.subsystemA.part3;
    set System.subsystemA.part3.quantity = 3;
    select System.subsystemB.part5;
    select System.subsystemB.part6;
    set System.subsystemB.part5.behaviorVariant2;
  }

  constraint block SystemConstraint {
    parameter minPartsA : Integer = 1;
    parameter maxPartsA : Integer = 3;
    constraint subsystemA.part3.size >= minPartsA and subsystemA.part3.size <= maxPartsA;
    constraint if (subsystemB.part5.behaviorVariant1) then (subsystemB.portB.isConnected);
  }
}

