    note "Specifies minimum reliability/durability requirements for vehicle lifecycle";  
  }  

  requirement VehicleMassRequirement {  
    text "Vehicle must have a defined mass grade (2000 kg or 2500 kg) and actual mass must not exceed maximum mass limitation";  
    satisfy mass : MassGradeConstraint;  
  }  

