    verify interface : DriveInterface;  
  }  

  requirement ReliabilityPerformanceRequirement {  
    text "Vehicle must maintain specified reliability and durability throughout its lifecycle";  
    satisfy reliability : ReliabilityConstraint;  
  }  

  decomposition VehicleDecomposition {  
    part engine : Engine;  
    part transmission : Transmission;  
    part driveInterface : DriveInterface;  
  }  

  allocation VehicleMassAllocation {  
    allocate VehicleMassRequirement to Vehicle;  
