module VehicleMassCompliance {  
  class Vehicle {  
    property actualMass : Real;  
  }  

  class WeighingEquipment {  
    behavior measureMass(vehicle: Vehicle): Real {  
      return vehicle.actualMass;  
    }  
  }  

  class TestOperator {  
    behavior operateEquipment(equipment: WeighingEquipment, vehicle: Vehicle): Real {  
      return equipment.measureMass(vehicle);  
    }  
  }  

  class ComplianceCheckSystem {  
    parameter maxPermissibleMass : Real;  
    constraint maxPermissibleMass > 0;  

    behavior determineCompliance(vehicle: Vehicle): Boolean {  
      return vehicle.actualMass <= maxPermissibleMass;  
    }  

    behavior processVehicles(vehicles: List<Vehicle>): List<Boolean> {  
      var results := List<Boolean>();  
      for (v in vehicles) {  
        results.add(determineCompliance(v));  
      }  
      return results;  
    }  
  }  
}

