model AutomotiveSystem {
  block Car {
    property totalMass : Mass = 1200 kg;
    property length : Length = 4.82 m;
    part wheelAssembly : WheelAssembly [4];
  }

  block WheelAssembly {
    part wheel : Wheel;
    part brakeDisc : BrakeDisc;
    constraint brakeDiscFit : BrakeDiscFitConstraint {
      parameter wheelOuterDiameter : Length = wheel.outerDiameter;
      parameter brakeDiscDiameter : Length = brakeDisc.diameter;
      constraint brakeDiscDiameter < wheelOuterDiameter;
    }
  }

  block Wheel {
    part rim : Rim;
    part tire : Tire;
    property outerDiameter : Length;
    constraint outerDiameterCalc : OuterDiameterConstraint {
      parameter rimDiameter : Length = rim.diameter;
      parameter tireHeight : Length = tire.height;
      parameter calculatedOuterDiameter : Length = rimDiameter * 25.4 + 2 * tireHeight;
      constraint outerDiameter = calculatedOuterDiameter;
    }
  }

  block Rim {
    property diameter : Length = 18 in;
  }

  block Tire {
    property width : Length = 245 mm;
    property height : Length = 45 mm;
    property treadDepth : Length = 6.0 mm;
    constraint minTreadDepth : TreadDepthConstraint {
      parameter td : Length = treadDepth;
      constraint td >= 3.5 mm;
    }
  }

  block BrakeDisc {
    property radius : Length = 95 mm;
    property diameter : Length;
    constraint diameterCalc : DiameterConstraint {
      parameter r : Length = radius;
      parameter d : Length = diameter;
      constraint d = 2 * r;
    }
  }

  constraint Block OuterDiameterConstraint {
    parameter rimDiameter : Length;
    parameter tireHeight : Length;
    parameter calculatedOuterDiameter : Length = rimDiameter * 25.4 + 2 * tireHeight;
    constraint outerDiameter = calculatedOuterDiameter;
  }

  constraint Block BrakeDiscFitConstraint {
    parameter wheelOuterDiameter : Length;
    parameter brakeDiscDiameter : Length;
    constraint brakeDiscDiameter < wheelOuterDiameter;
  }

  constraint Block TreadDepthConstraint {
    parameter treadDepth : Length;
    constraint treadDepth >= 3.5 mm;
  }

  constraint Block DiameterConstraint {
    parameter radius : Length;
    parameter diameter : Length;
    constraint diameter = 2 * radius;
  }
}

