model PubSubModel {
  class Producer {
    behavior publishMessage(topic: Topic, message: Message) {
      action publish(topic, message)
    }
  }

  class Consumer {
    behavior subscribeToTopic(topic: Topic) {
      action subscribe(topic)
    }

    behavior receiveMessage(message: Message) {
      action receive(message)
    }
  }

  class Server {
    behavior distributeMessage(topic: Topic, message: Message) {
      action checkSubscriptions(topic)
      for (subscription in Server.subscriptions where subscription.topic == topic) {
        action deliverMessage(subscription.consumer, message)
      }
    }

    behavior manageSubscription(consumer: Consumer, topic: Topic) {
      action addSubscription(consumer, topic)
    }
  }

  class Topic {
    property name: String
  }

  class Message {
    property content: String
  }

  class Subscription {
    property consumer: Consumer
    property topic: Topic
  }

  association ProducerToServer {
    Producer.producerSendsToServer : Producer --> Server
  }

  association ConsumerToServer {
    Server.serverManagesConsumers : Server --> Consumer
  }

  association TopicToServer {
    Server.serverManagesTopics : Server --> Topic
  }

  association SubscriptionLink {
    Subscription.subscriptionOfTopic : Subscription --> Topic
    Subscription.subscriptionByConsumer : Subscription --> Consumer
  }

  association MessageDelivery {
    Server.serverSendsMessages : Server --> Message
    Consumer.consumerReceivesMessages : Consumer --> Message
  }
}

