block Vehicle {
    property mass : Real;
    property initialPosition : Real;
    property initialVelocity : Real;
    property timeInterval : Real;
    property powerInputs : Sequence<PowerInput>;
    property trajectory : Sequence<Real>;
}constraint VehicleSimulation {
    variable velocity : Sequence<Real>;
    variable position : Sequence<Real>;
    equation
        velocity[0] = initialVelocity;
        position[0] = initialPosition;
        for i in 0..size(powerInputs)-1 loop
            0.5 * mass * velocity[i+1]^2 = 0.5 * mass * velocity[i]^2 + powerInputs[i].powerValue * timeInterval;
            position[i+1] = position[i] + (velocity[i] + velocity[i+1]) / 2 * timeInterval;
        end for;
        trajectory = position;
    end equation;
}
