        }
        transition to Maintenance when at (maintenanceScheduler.nextMaintenanceTime);
      }
      state Degraded {
        transition to Normal when (temperatureSensor.currentTemperature < controller.maxAllowableTemperature);
        transition to Maintenance when at (maintenanceScheduler.nextMaintenanceTime);
      }
      state Maintenance {
        after (maintenanceScheduler.maintenanceDuration) transition to Normal {
          maintenanceScheduler.nextMaintenanceTime = currentTime + maintenanceScheduler.maintenanceInterval;
        }
      }
    }
  }
}

