model VehicleDynamicsModel {
  block VehicleDynamicsSystem {
    wheelPower : Power;
    dragCoefficient : Dimensionless;
    frictionCoefficient : Dimensionless;
    mass : Mass;
    speed : Speed;
    availablePower : Power;
    acceleration : Acceleration;
    time : Time;
    position : Length;
    initialSpeed : Speed;
    initialPosition : Length;
  }

  value model VehicleDynamicsVM {
    base unit set SI;
    wheelPower : Power;
    dragCoefficient : Dimensionless;
    frictionCoefficient : Dimensionless;
    mass : Mass;
    speed : Speed;
    availablePower : Power;
    acceleration : Acceleration;
    time : Time;
    position : Length;
    initialSpeed : Speed;
    initialPosition : Length;
    gravity : Acceleration = 9.81 m/s;

    constraint availablePower = wheelPower - (0.5 * dragCoefficient * speed + frictionCoefficient * mass * gravity * speed);
    constraint acceleration = (availablePower / (mass * speed)) - (0.5 * dragCoefficient * speed / mass) - frictionCoefficient * gravity;
    constraint speed = initialSpeed + acceleration * time;
    constraint position = initialPosition + initialSpeed * time + 0.5 * acceleration * time;
  }
}

model VehicleDrivingDynamicsModel {
  block VehicleDrivingDynamicsSystem {
    vehicleMass: Real;
    wheelPower: Real;
    dragCoefficient: Real;
    rollingResistanceCoefficient: Real;
    initialSpeed: Real;
    initialPosition: Length;
    currentSpeed: Real;
    currentPosition: Length;
    currentTime: Duration;
    timeStep: Duration;
  }
  behavior simulateDynamics {
    dynamic state DrivingSimulation {
      currentSpeed := initialSpeed;
      currentPosition := initialPosition;
      currentTime := 0.0;
      loop {
        dragForce := 0.5 * dragCoefficient * currentSpeed^2;
        rollingResistance := rollingResistanceCoefficient * vehicleMass * 9.81;
        netForce := (wheelPower / currentSpeed) - dragForce - rollingResistance;
        acceleration := netForce / vehicleMass;
        currentSpeed := currentSpeed + acceleration * timeStep;
        currentPosition := currentPosition + currentSpeed * timeStep + 0.5 * acceleration * timeStep^2;
        currentTime := currentTime + timeStep;
      }
    }
  }
}

model VehicleDynamicModel {
