    frictionCoefficient : Real;
    acceleration : Real;
    finalVelocity : Real;
    finalPosition : Real;
  }

  constraint block VehicleDynamicConstraint {
    acceleration = (drivePower / initialVelocity - 0.5 * dragCoefficient * initialVelocity^2 - frictionCoefficient * mass * 9.81) / mass;
    finalVelocity = initialVelocity + acceleration * timeInterval;
    finalPosition = initialPosition + initialVelocity * timeInterval + 0.5 * acceleration * timeInterval^2;
  }
}

