model VehicleDynamicSimulation {
  block VehicleDynamicSystem {
    property initialVelocity : Real;
    property initialPosition : Real;
    property acceleration : Real;
    property timeStep : Real;
    property modelCenterTool : ModelCenterAnalysisTool;
    property finalVelocity : Real;
    property finalPosition : Real;
    constraint c1 : VelocityConstraint {
      param vFinal : Real = self.finalVelocity;
      param vInitial : Real = self.initialVelocity;
      param a : Real = self.acceleration;
      param dt : Real = self.timeStep;
      vFinal = vInitial + a * dt;
    };
    constraint c2 : PositionConstraint {
      param pFinal : Real = self.finalPosition;
      param pInitial : Real = self.initialPosition;
      param vInitial : Real = self.initialVelocity;
      param a : Real = self.acceleration;
      param dt : Real = self.timeStep;
      pFinal = pInitial + vInitial * dt + 0.5 * a * dt^2;
    };
  };
  block ModelCenterAnalysisTool {
  };
  constraint VelocityConstraint {
    param vFinal : Real;
    param vInitial : Real;
    param a : Real;
    param dt : Real;
    vFinal = vInitial + a * dt;
  };
  constraint PositionConstraint {
    param pFinal : Real;
    param pInitial : Real;
    param vInitial : Real;
    param a : Real;
    param dt : Real;
    pFinal = pInitial + vInitial * dt + 0.5 * a * dt^2;
  };
};

