}constraint LowBatteryLevelConstraint {
  check (battery.level <= minimumThreshold);
}constraint BatteryAgingConstraint {
  check (battery.age > agingThreshold);
}constraint ProlongedLowBatteryConstraint {
  check (LowBatteryLevelConstraint and duration > thresholdTime);
}constraint SeverityLevelConstraint {
