  consequence: equipment enters low battery state;
}

failure scenario DeviceShutdownFailureScenario {
  condition: uses ProlongedLowBatteryConstraint;
  consequence: device shutdown;
}

BatteryAgingRisk results in LowBatteryLevelRisk;
LowBatteryLevelRisk results in DeviceShutdownRisk;

Equipment::monitoringSystem satisfies MinimumBatteryLevelConstraint;
Equipment::monitoringSystem satisfies LowBatteryLevelConstraint;
Equipment::monitoringSystem satisfies BatteryAgingConstraint;

LowBatteryLevelFailureScenario satisfies LowBatteryLevelRisk;
DeviceShutdownFailureScenario satisfies DeviceShutdownRisk;
DeviceShutdownFailureScenario refines LowBatteryLevelFailureScenario;

DeviceShutdownRisk satisfies SeverityLevelConstraint;
