    port controlPort requires ControlInterface;
  }

  block ComponentB {
    port dataPort uses DataInterface;
    port controlPort provides ControlInterface;
  }

  interface DataInterface {
    operation transmitData(data : String);
    operation receiveData(data : String);
  }

  interface ControlInterface {
    operation sendCommand(command : String);
    operation acknowledgeStatus(status : Boolean);
  }

  block Components {
    part subComponentA : SubComponentA;
    part subComponentB : SubComponentB;
  }

  block SubComponentA {
    port interfacePort uses DataInterface;
  }

  block SubComponentB {
    port interfacePort provides ControlInterface;
  }
}
